package com.sweet.modular.card.mapper;import com.sweet.modular.card.entity.Card;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * <p> * 卡项 Mapper 接口 * </p> * * @author admin * @since 2020-04-28 */public interface CardMapper extends BaseMapper<Card> {    public Page<Card> customPageList(Page page, @Param("param") Card card);    @Select("select * from business_card where no = #{no}")    public Card getCardByNo(@Param("no")String no);}