package com.sweet.modular.card.service.impl;import com.sweet.modular.card.entity.Card;import com.sweet.modular.card.mapper.CardMapper;import com.sweet.modular.card.service.CardService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 卡项 服务实现类 * </p> * * @author admin * @since 2020-04-28 */@Servicepublic class CardServiceImpl extends ServiceImpl<CardMapper, Card> implements CardService {    @Override    public LayuiPageInfo findPageBySpec(Card card) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, card);        return LayuiPageFactory.createPageInfo(page);    }}