package com.sweet.modular.cashier.service;import com.sweet.modular.cashier.model.CardForm;import com.sweet.modular.cashier.model.CashierForm;import com.sweet.modular.member.entity.Member;import com.sweet.modular.membercard.entity.Membercard;import com.sweet.modular.sell.entity.Sell;import com.sweet.modular.selldetail.entity.Selldetail;import com.sweet.modular.sellpay.entity.Sellpay;import java.math.BigDecimal;import java.util.List;public interface CashierService {    /**     * 创建销售总单     * @return     */    public Sell addSell(Member member, BigDecimal amount,List<Sellpay> sellpayList, Integer SellType);    /**     * 创建销售细单     * @param sellId     * @param amount     * @param SellType     * @param sellForm     * @return     */    public Selldetail addSellDetail(Long sellId,BigDecimal amount, Integer SellType, CashierForm sellForm);    /**     * 创建销售总店和支付方式关联     * @param sellId     * @param sellpayList     * @return     */    public Sellpay addSellPay(Long sellId, List<Sellpay> sellpayList);    /**     * 创建会员卡     * @param member     * @param sellForm     * @return     */    public Membercard buildMemberCard(Member member, CardForm sellForm);    public Membercard chargedMemberCard(Member member, CardForm sellForm);    public Membercard deductMemberCard(Membercard card,List<Sellpay> sellpayList);}