package com.sweet.modular.category.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.category.entity.Category;import com.sweet.modular.category.service.CategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;import java.util.List;/** * <p> * 项目，卖品分类 前端控制器 * </p> * * @author admin * @since 2020-05-06 */@Controller@RequestMapping("/admin/category")public class CategoryController {    @Autowired    CategoryService categoryService;    /**     * 列表页     */    @RequestMapping("/category_list")    public String list(){        return "/modular/category/category_list";    }    /**     * 编辑页     */    @RequestMapping("/category_edit")    public String edit(){        return "/modular/category/category_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editCategory")    @ResponseBody    public ResultBean editCategory(Category category){        categoryService.saveOrUpdate(category);        return ResultBean.success(category);    }    /**     * 删除     */    @RequestMapping("/delCategory")    @ResponseBody    public ResultBean delCategory(Category category){        categoryService.removeById(category);        return ResultBean.success(category);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getCategoryDetail")    @ResponseBody    public ResultBean getCategoryDetail(String id){        Category category = categoryService.getById(id);        return ResultBean.success(category);    }    /**     * 列表数据     */    @RequestMapping("/getCategoryList")    @ResponseBody    public LayuiPageInfo getCategoryList(Category Category){        LayuiPageInfo pageInfo = categoryService.findPageBySpec(Category);        return pageInfo;    }    @RequestMapping("/getCategoryByType")    @ResponseBody    public ResultBean getCategoryByType(Integer categoryType){        List<Category> list = categoryService.getCategoryByType(categoryType);        return ResultBean.success(list);    }}