package com.sweet.modular.category.mapper;import com.sweet.modular.category.entity.Category;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 项目，卖品分类 Mapper 接口 * </p> * * @author admin * @since 2020-05-06 */public interface CategoryMapper extends BaseMapper<Category> {    public Page<Category> customPageList(Page page, @Param("param") Category category);    @Select("select * from business_category where category_type = #{CategoryType} order by create_time asc")    public List<Category> getCategoryByType(@Param("CategoryType")Integer CategoryType);}