package com.sweet.modular.category.service.impl;import com.sweet.modular.category.entity.Category;import com.sweet.modular.category.mapper.CategoryMapper;import com.sweet.modular.category.service.CategoryService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 项目，卖品分类 服务实现类 * </p> * * @author admin * @since 2020-05-06 */@Servicepublic class CategoryServiceImpl extends ServiceImpl<CategoryMapper, Category> implements CategoryService {    @Override    public LayuiPageInfo findPageBySpec(Category category) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, category);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public List<Category> getCategoryByType(Integer CategoryType) {        return baseMapper.getCategoryByType(CategoryType);    }}