package com.sweet.modular.inventory.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.inventory.entity.Inventory;import com.sweet.modular.inventory.service.InventoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 库存表 前端控制器 * </p> * * @author admin * @since 2020-06-22 */@Controller@RequestMapping("/admin/inventory")public class InventoryController {    @Autowired    InventoryService inventoryService;    /**     * 列表页     */    @RequestMapping("/inventory_list")    public String list(){        return "/modular/inventory/inventory_list";    }    /**     * 编辑页     */    @RequestMapping("/inventory_edit")    public String edit(){        return "/modular/inventory/inventory_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editInventory")    @ResponseBody    public ResultBean editInventory(Inventory inventory){        inventoryService.saveOrUpdate(inventory);        return ResultBean.success(inventory);    }    /**     * 删除     */    @RequestMapping("/delInventory")    @ResponseBody    public ResultBean delInventory(Inventory inventory){        inventoryService.removeById(inventory);        return ResultBean.success(inventory);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getInventoryDetail")    @ResponseBody    public ResultBean getInventoryDetail(String id){        Inventory inventory = inventoryService.getById(id);        return ResultBean.success(inventory);    }    /**     * 列表数据     */    @RequestMapping("/getInventoryList")    @ResponseBody    public LayuiPageInfo getInventoryList(Inventory Inventory){        LayuiPageInfo pageInfo = inventoryService.findPageBySpec(Inventory);        return pageInfo;    }}