package com.sweet.modular.inventory.mapper;import com.sweet.modular.inventory.entity.Inventory;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * <p> * 库存表 Mapper 接口 * </p> * * @author admin * @since 2020-06-22 */public interface InventoryMapper extends BaseMapper<Inventory> {    public Page<Inventory> customPageList(Page page, @Param("param") Inventory inventory);    @Select("select * from business_inventory where dept_id=#{deptId} and product_id=#{productId}")    public Inventory getInventoryByDeptId(@Param("deptId")String deptId,@Param("productId")Long productId);}