package com.sweet.modular.member.mapper;import com.sweet.modular.member.entity.Member;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * <p> * 会员表 Mapper 接口 * </p> * * @author admin * @since 2020-04-25 */public interface MemberMapper extends BaseMapper<Member> {    public Page<Member> customPageList(Page page, @Param("param") Member member);    @Select("select * from business_member where phone_number = #{phoneNumber} and dept_id = #{deptId}")    public Member getMemberByPhone(@Param("phoneNumber")String phoneNumber,@Param("deptId")String deptId);    @Select("select count(1) from business_member where create_time between  #{beginTime} and #{endTime} and dept_id = #{deptId}")    public Integer getMemberCountByTime(@Param("beginTime") String beginTime,@Param("endTime")String endTime,@Param("deptId")String deptId);}