package com.sweet.modular.member.service.impl;import com.sweet.modular.member.entity.Member;import com.sweet.modular.member.mapper.MemberMapper;import com.sweet.modular.member.service.MemberService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 会员表 服务实现类 * </p> * * @author admin * @since 2020-04-25 */@Servicepublic class MemberServiceImpl extends ServiceImpl<MemberMapper, Member> implements MemberService {    @Override    public LayuiPageInfo findPageBySpec(Member member) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, member);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public Member getMemberByPhone(String phoneNumber,String deptId) {        return baseMapper.getMemberByPhone(phoneNumber,deptId);    }    @Override    public Integer getMemberCountByTime(String beginTime, String endTime,String deptId) {        return baseMapper.getMemberCountByTime(beginTime,endTime, deptId);    }}