package com.sweet.modular.membercard.controller;import com.sweet.core.shiro.ShiroKit;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.membercard.entity.Membercard;import com.sweet.modular.membercard.service.MembercardService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 会员卡关联表 前端控制器 * </p> * * @author admin * @since 2020-04-30 */@Controller@RequestMapping("/admin/membercard")public class MembercardController {    @Autowired    MembercardService membercardService;    /**     * 列表页     */    @RequestMapping("/membercard_list")    public String list(){        return "/modular/membercard/membercard_list";    }    /**     * 编辑页     */    @RequestMapping("/membercard_edit")    public String edit(){        return "/modular/membercard/membercard_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editMembercard")    @ResponseBody    public ResultBean editMembercard(Membercard membercard){        membercardService.saveOrUpdate(membercard);        return ResultBean.success(membercard);    }    /**     * 删除     */    @RequestMapping("/delMembercard")    @ResponseBody    public ResultBean delMembercard(Membercard membercard){        membercardService.removeById(membercard);        return ResultBean.success(membercard);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getMembercardDetail")    @ResponseBody    public ResultBean getMembercardDetail(String id){        Membercard membercard = membercardService.getById(id);        return ResultBean.success(membercard);    }    /**     * 列表数据     */    @RequestMapping("/getMembercardList")    @ResponseBody    public LayuiPageInfo getMembercardList(Membercard Membercard){        Membercard.setDeptId(ShiroKit.getUser().getDeptId());        LayuiPageInfo pageInfo = membercardService.findPageBySpec(Membercard);        return pageInfo;    }}