package com.sweet.modular.membercard.mapper;import com.sweet.modular.membercard.entity.Membercard;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 会员卡关联表 Mapper 接口 * </p> * * @author admin * @since 2020-04-30 */public interface MembercardMapper extends BaseMapper<Membercard> {    public Page<Membercard> customPageList(Page page, @Param("param") Membercard membercard);    @Select("select * from business_membercard where member_id = #{memberId} order by amount desc")    public List<Membercard> getMemberCardByMemberId(String memberId);}