package com.sweet.modular.membercard.service.impl;import com.sweet.modular.membercard.entity.Membercard;import com.sweet.modular.membercard.mapper.MembercardMapper;import com.sweet.modular.membercard.service.MembercardService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 会员卡关联表 服务实现类 * </p> * * @author admin * @since 2020-04-30 */@Servicepublic class MembercardServiceImpl extends ServiceImpl<MembercardMapper, Membercard> implements MembercardService {    @Override    public LayuiPageInfo findPageBySpec(Membercard membercard) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, membercard);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public List<Membercard> getMemberCardByMemberId(String memberId) {        return baseMapper.getMemberCardByMemberId(memberId);    }}