package com.sweet.modular.product.mapper;import com.sweet.modular.product.entity.Product;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 商品，项目表 Mapper 接口 * </p> * * @author admin * @since 2020-05-06 */public interface ProductMapper extends BaseMapper<Product> {    public Page<Product> customPageList(Page page, @Param("param") Product product);    @Select("select * from business_product where product_no =#{productNo} and product_type = ${productType}")    public Product findProductByNo(@Param("productNo")String productNo,@Param("productType") Integer productType);    @Select("select * from business_product order by  product_no asc")    public List<Product> getProductList();}