package com.sweet.modular.product.service.impl;import com.sweet.modular.product.entity.Product;import com.sweet.modular.product.mapper.ProductMapper;import com.sweet.modular.product.service.ProductService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 商品，项目表 服务实现类 * </p> * * @author admin * @since 2020-05-06 */@Servicepublic class ProductServiceImpl extends ServiceImpl<ProductMapper, Product> implements ProductService {    @Override    public LayuiPageInfo findPageBySpec(Product product) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, product);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public Product findProductByNo(String productNo,Integer productType) {        return baseMapper.findProductByNo(productNo,productType);    }    @Override    public List<Product> getProductList() {        return baseMapper.getProductList();    }}