package com.sweet.modular.receipt.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.receipt.entity.Receipt;import com.sweet.modular.receipt.service.ReceiptService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 条码支付 前端控制器 * </p> * * @author admin * @since 2020-07-07 */@Controller@RequestMapping("/admin/receipt")public class ReceiptController {    @Autowired    ReceiptService receiptService;    /**     * 列表页     */    @RequestMapping("/receipt_list")    public String list(){        return "/modular/receipt/receipt_list";    }    /**     * 编辑页     */    @RequestMapping("/receipt_edit")    public String edit(){        return "/modular/receipt/receipt_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editReceipt")    @ResponseBody    public ResultBean editReceipt(Receipt receipt){        receiptService.saveOrUpdate(receipt);        return ResultBean.success(receipt);    }    /**     * 删除     */    @RequestMapping("/delReceipt")    @ResponseBody    public ResultBean delReceipt(Receipt receipt){        receiptService.removeById(receipt);        return ResultBean.success(receipt);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getReceiptDetail")    @ResponseBody    public ResultBean getReceiptDetail(String id){        Receipt receipt = receiptService.getById(id);        return ResultBean.success(receipt);    }    /**     * 列表数据     */    @RequestMapping("/getReceiptList")    @ResponseBody    public LayuiPageInfo getReceiptList(Receipt Receipt){        LayuiPageInfo pageInfo = receiptService.findPageBySpec(Receipt);        return pageInfo;    }}