package com.sweet.modular.receipt.mapper;import com.sweet.modular.receipt.entity.Receipt;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;/** * <p> * 条码支付 Mapper 接口 * </p> * * @author admin * @since 2020-07-07 */public interface ReceiptMapper extends BaseMapper<Receipt> {    public Page<Receipt> customPageList(Page page, @Param("param") Receipt receipt);    @Select("select * from business_receipt where trade_no =#{tradeNo}")    public Receipt getReceiptByTradeNo(@Param("tradeNo") String tradeNo);}