package com.sweet.modular.receipt.service.impl;import com.sweet.modular.receipt.entity.Receipt;import com.sweet.modular.receipt.mapper.ReceiptMapper;import com.sweet.modular.receipt.service.ReceiptService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 条码支付 服务实现类 * </p> * * @author admin * @since 2020-07-07 */@Servicepublic class ReceiptServiceImpl extends ServiceImpl<ReceiptMapper, Receipt> implements ReceiptService {    @Override    public LayuiPageInfo findPageBySpec(Receipt receipt) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, receipt);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public Receipt getReceiptByTradeNo(String tradeNo) {        return baseMapper.getReceiptByTradeNo(tradeNo);    }}