package com.sweet.modular.sell.mapper;import com.sweet.modular.sell.entity.Sell;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.sweet.modular.sell.model.SellFull;import com.sweet.modular.sell.model.SellSort;import com.sweet.modular.selldetail.entity.Selldetail;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.math.BigDecimal;import java.util.List;import java.util.Map;/** * <p> * 销售表 Mapper 接口 * </p> * * @author admin * @since 2020-04-30 */public interface SellMapper extends BaseMapper<Sell> {    public Page<Sell> customPageList(Page page, @Param("param") Sell sell,@Param("beginTime") String beginTime,@Param("endTime")String endTime);    @Select("select a.*,c.push_money from business_selldetail a left join business_sell b on a.sell_id = b.id LEFT JOIN business_detail_tech c on c.SELL_DETAIL_ID = a.id " +            "where c.tech_id =#{userId} " +            "and a.create_time between  #{beginTime} and #{endTime} and b.status = 1  order by a.create_time desc")    public List<Selldetail> getSellDetail(@Param("userId")String userId, @Param("beginTime") String beginTime, @Param("endTime")String endTime);    public List<Map<String,String>> getSellAmountByDate(@Param("days")int days,@Param("deptId")String deptId);    public SellFull getSellAmountByYear(@Param("deptId")String deptId);    public SellFull getSellAmountByMonth(@Param("deptId")String deptId);    public List<SellSort> getSellSort(@Param("deptId")String deptId);    public List<Map<String,String>> getPayTypePercent(@Param("deptId")String deptId);    @Select("SELECT * FROM business_selldetail sd LEFT JOIN business_sell s ON s.id = sd.sell_id WHERE sd.SELL_TYPE = #{type} AND sd.CARD_ID = #{id}")    public List<Map> findSelltypeByid(@Param("type")Integer type,@Param("id")Long id);}