package com.sweet.modular.selldetail.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.selldetail.entity.Selldetail;import com.sweet.modular.selldetail.service.SelldetailService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 销售详情 前端控制器 * </p> * * @author admin * @since 2020-04-30 */@Controller@RequestMapping("/admin/selldetail")public class SelldetailController {    @Autowired    SelldetailService selldetailService;    /**     * 列表页     */    @RequestMapping("/selldetail_list")    public String list(){        return "/modular/selldetail/selldetail_list";    }    /**     * 编辑页     */    @RequestMapping("/selldetail_edit")    public String edit(){        return "/modular/selldetail/selldetail_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editSelldetail")    @ResponseBody    public ResultBean editSelldetail(Selldetail selldetail){        selldetailService.saveOrUpdate(selldetail);        return ResultBean.success(selldetail);    }    /**     * 删除     */    @RequestMapping("/delSelldetail")    @ResponseBody    public ResultBean delSelldetail(Selldetail selldetail){        selldetailService.removeById(selldetail);        return ResultBean.success(selldetail);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getSelldetailDetail")    @ResponseBody    public ResultBean getSelldetailDetail(String id){        Selldetail selldetail = selldetailService.getById(id);        return ResultBean.success(selldetail);    }    /**     * 列表数据     */    @RequestMapping("/getSelldetailList")    @ResponseBody    public LayuiPageInfo getSelldetailList(Selldetail Selldetail){        LayuiPageInfo pageInfo = selldetailService.findPageBySpec(Selldetail);        return pageInfo;    }}