package com.sweet.modular.selldetailTech.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.selldetailTech.entity.DetailTech;import com.sweet.modular.selldetailTech.service.DetailTechService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 销售详情与服务技术关系表 前端控制器 * </p> * * @author admin * @since 2020-07-03 */@Controller@RequestMapping("/admin/selldetailTech")public class DetailTechController {    @Autowired    DetailTechService selldetailTechService;    /**     * 列表页     */    @RequestMapping("/selldetailTech_list")    public String list(){        return "/modular/selldetailTech/selldetailTech_list";    }    /**     * 编辑页     */    @RequestMapping("/selldetailTech_edit")    public String edit(){        return "/modular/selldetailTech/selldetailTech_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editDetailTech")    @ResponseBody    public ResultBean editDetailTech(DetailTech selldetailTech){        selldetailTechService.saveOrUpdate(selldetailTech);        return ResultBean.success(selldetailTech);    }    /**     * 删除     */    @RequestMapping("/delDetailTech")    @ResponseBody    public ResultBean delDetailTech(DetailTech selldetailTech){        selldetailTechService.removeById(selldetailTech);        return ResultBean.success(selldetailTech);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getDetailTechDetail")    @ResponseBody    public ResultBean getDetailTechDetail(String id){        DetailTech selldetailTech = selldetailTechService.getById(id);        return ResultBean.success(selldetailTech);    }    /**     * 列表数据     */    @RequestMapping("/getDetailTechList")    @ResponseBody    public LayuiPageInfo getDetailTechList(DetailTech DetailTech){        LayuiPageInfo pageInfo = selldetailTechService.findPageBySpec(DetailTech);        return pageInfo;    }}