package com.sweet.modular.selldetailTech.service.impl;import com.sweet.modular.selldetailTech.entity.DetailTech;import com.sweet.modular.selldetailTech.mapper.DetailTechMapper;import com.sweet.modular.selldetailTech.service.DetailTechService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 销售详情与服务技术关系表 服务实现类 * </p> * * @author admin * @since 2020-07-03 */@Servicepublic class DetailTechServiceImpl extends ServiceImpl<DetailTechMapper, DetailTech> implements DetailTechService {    @Override    public LayuiPageInfo findPageBySpec(DetailTech selldetailTech) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, selldetailTech);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public int saveBySelectByUserId(DetailTech detailTech) {        return baseMapper.saveBySelectByUserId(detailTech);    }}