package com.sweet.modular.sellpay.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.sellpay.entity.Sellpay;import com.sweet.modular.sellpay.service.SellpayService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 销售单付款方式 前端控制器 * </p> * * @author admin * @since 2020-04-30 */@Controller@RequestMapping("/admin/sellpay")public class SellpayController {    @Autowired    SellpayService sellpayService;    /**     * 列表页     */    @RequestMapping("/sellpay_list")    public String list(){        return "/modular/sellpay/sellpay_list";    }    /**     * 编辑页     */    @RequestMapping("/sellpay_edit")    public String edit(){        return "/modular/sellpay/sellpay_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editSellpay")    @ResponseBody    public ResultBean editSellpay(Sellpay sellpay){        sellpayService.saveOrUpdate(sellpay);        return ResultBean.success(sellpay);    }    /**     * 删除     */    @RequestMapping("/delSellpay")    @ResponseBody    public ResultBean delSellpay(Sellpay sellpay){        sellpayService.removeById(sellpay);        return ResultBean.success(sellpay);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getSellpayDetail")    @ResponseBody    public ResultBean getSellpayDetail(String id){        Sellpay sellpay = sellpayService.getById(id);        return ResultBean.success(sellpay);    }    /**     * 列表数据     */    @RequestMapping("/getSellpayList")    @ResponseBody    public LayuiPageInfo getSellpayList(Sellpay Sellpay){        LayuiPageInfo pageInfo = sellpayService.findPageBySpec(Sellpay);        return pageInfo;    }}