package com.sweet.modular.sellpay.service.impl;import com.sweet.core.util.RedisUtil;import com.sweet.modular.sellpay.entity.Sellpay;import com.sweet.modular.sellpay.mapper.SellpayMapper;import com.sweet.modular.sellpay.service.SellpayService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;import java.util.List;/** * <p> * 销售单付款方式 服务实现类 * </p> * * @author admin * @since 2020-04-30 */@Servicepublic class SellpayServiceImpl extends ServiceImpl<SellpayMapper, Sellpay> implements SellpayService {    @Autowired    RedisUtil redisUtil;    @Override    public LayuiPageInfo findPageBySpec(Sellpay sellpay) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, sellpay);        return LayuiPageFactory.createPageInfo(page);    }    @Override    public List<Sellpay> findBySellId(Long sellId) {        String redisKey = "sell_pay"+sellId;        List<Sellpay> list = (List<Sellpay>) redisUtil.get(redisKey);        if(list==null){            list = baseMapper.findBySellId(sellId);            redisUtil.set(redisKey,list);        }        return list;    }}