package com.sweet.modular.stock.service.impl;import com.sweet.modular.stock.entity.Stock;import com.sweet.modular.stock.mapper.StockMapper;import com.sweet.modular.stock.service.StockService;import com.baomidou.mybatisplus.extension.service.impl.ServiceImpl;import org.springframework.stereotype.Service;import com.baomidou.mybatisplus.core.metadata.IPage;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.sweet.core.model.system.LayuiPageFactory;import com.sweet.core.model.system.LayuiPageInfo;/** * <p> * 入库单 服务实现类 * </p> * * @author admin * @since 2020-06-18 */@Servicepublic class StockServiceImpl extends ServiceImpl<StockMapper, Stock> implements StockService {    @Override    public LayuiPageInfo findPageBySpec(Stock stock) {        Page pageContext = LayuiPageFactory.defaultPage();        IPage page = this.baseMapper.customPageList(pageContext, stock);        return LayuiPageFactory.createPageInfo(page);    }}