package com.sweet.modular.stockDetail.controller;import com.sweet.core.translationDict.DictParam;import com.sweet.core.translationDict.TranslationDict;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.model.ResultBean;import com.sweet.core.model.system.LayuiPageInfo;import com.sweet.modular.stockDetail.entity.StockDetail;import com.sweet.modular.stockDetail.service.StockDetailService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import com.sweet.core.util.StringUtil;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.stereotype.Controller;/** * <p> * 入库单详情 前端控制器 * </p> * * @author admin * @since 2020-06-20 */@Controller@RequestMapping("/admin/stockDetail")public class StockDetailController {    @Autowired    StockDetailService stockDetailService;    /**     * 列表页     */    @RequestMapping("/stockDetail_list")    public String list(){        return "/modular/stockDetail/stockDetail_list";    }    /**     * 编辑页     */    @RequestMapping("/stockDetail_edit")    public String edit(){        return "/modular/stockDetail/stockDetail_edit";    }    /**     * 添加/编辑     */    @RequestMapping("/editStockDetail")    @ResponseBody    public ResultBean editStockDetail(StockDetail stockDetail){        stockDetailService.saveOrUpdate(stockDetail);        return ResultBean.success(stockDetail);    }    /**     * 删除     */    @RequestMapping("/delStockDetail")    @ResponseBody    public ResultBean delStockDetail(StockDetail stockDetail){        stockDetailService.removeById(stockDetail);        return ResultBean.success(stockDetail);    }    /**     * 添加修改菜单     * @param menu     * @return     */    @RequestMapping("/getStockDetailDetail")    @ResponseBody    public ResultBean getStockDetailDetail(String id){        StockDetail stockDetail = stockDetailService.getById(id);        return ResultBean.success(stockDetail);    }    /**     * 列表数据     */    @RequestMapping("/getStockDetailList")    @ResponseBody    public LayuiPageInfo getStockDetailList(StockDetail StockDetail){        LayuiPageInfo pageInfo = stockDetailService.findPageBySpec(StockDetail);        return pageInfo;    }}