package com.sweet.modular.stockDetail.mapper;import com.sweet.modular.stockDetail.entity.StockDetail;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import com.baomidou.mybatisplus.extension.plugins.pagination.Page;import com.baomidou.mybatisplus.core.mapper.BaseMapper;import org.apache.ibatis.annotations.Param;import org.apache.ibatis.annotations.Select;import java.util.List;/** * <p> * 入库单详情 Mapper 接口 * </p> * * @author admin * @since 2020-06-20 */public interface StockDetailMapper extends BaseMapper<StockDetail> {    public Page<StockDetail> customPageList(Page page, @Param("param") StockDetail stockDetail);    @Select("select * from business_stock_detail where stock_id = #{stockId}")    public List<StockDetail> getDetailByStockId(@Param("stockId") Long stockId);}